import { ref, onMounted, onUnmounted, h, render } from 'vue'
import Txt2ImgView from './views/Txt2ImgView.vue'
import ConfirmDialog from './components/ConfirmDialog.vue'
import { TOAST_DURATION } from './config/constants'

// Modal state
const showModal = ref(false)
const modalType = ref('') // 'comparison' or 'viewer'
const comparisonImage = ref('')
const currentImage = ref('') // Will be passed from Txt2ImgView

// Toast notification
const toasts = ref([])
let toastIdCounter = 0

/**
 * Show toast notification
 */
function showToast(message, type = 'info') {
  const id = toastIdCounter++
  toasts.value.push({ id, message, type })

  // íƒ€ì…ë³„ ì°¨ë“± ì‹œê°„ ì ìš©
  const duration = TOAST_DURATION[type] || TOAST_DURATION.info

  setTimeout(() => {
    const index = toasts.value.findIndex(t => t.id === id)
    if (index !== -1) {
      toasts.value.splice(index, 1)
    }
  }, duration)
}

/**
 * Remove toast manually
 */
function removeToast(id) {
  const index = toasts.value.findIndex(t => t.id === id)
  if (index !== -1) {
    toasts.value.splice(index, 1)
  }
}

/**
 * Open modal
 */
function openModal(type, image = null) {
  modalType.value = type
  if (type === 'comparison') {
    comparisonImage.value = image
  } else if (type === 'viewer') {
    if (!currentImage.value) return
  }
  showModal.value = true
}

/**
 * Close modal
 */
function closeModal() {
  showModal.value = false
  modalType.value = ''
  comparisonImage.value = ''
}

/**
 * Show confirm dialog
 */
function showConfirm(options) {
  const {
    title = 'í™•ì¸',
    message,
    confirmText = 'í™•ì¸',
    cancelText = 'ì·¨ì†Œ',
    onConfirm,
    onCancel
  } = options

  return new Promise((resolve) => {
    // Create container for the confirm dialog
    const container = document.createElement('div')
    document.body.appendChild(container)

    // Create VNode for ConfirmDialog
    const vnode = h(ConfirmDialog, {
      title,
      message,
      confirmText,
      cancelText,
      onConfirm: () => {
        resolve(true)
        onConfirm?.()
        cleanup()
      },
      onCancel: () => {
        resolve(false)
        onCancel?.()
        cleanup()
      }
    })

    // Render the dialog
    render(vnode, container)

    // Cleanup function
    function cleanup() {
      setTimeout(() => {
        render(null, container)
        document.body.removeChild(container)
      }, 200)
    }
  })
}

/**
 * Handle keyboard events for modal
 */
function handleKeyPress(event) {
  // ì…ë ¥ ìš”ì†Œì—ì„œëŠ” ë¬´ì‹œ
  const target = event.target
  if (target.tagName === 'INPUT' || target.tagName === 'TEXTAREA' || target.tagName === 'SELECT') {
    return
  }

  // ëª¨ë‹¬ì´ ì—´ë ¤ìˆì„ ë•Œë§Œ ì²˜ë¦¬
  if (showModal.value && (event.key === 'Escape' || event.key === ' ' || event.key === 'Enter')) {
    event.preventDefault()
    closeModal()
  }
}

// Set up keyboard listeners
onMounted(() => {
  window.addEventListener('keydown', handleKeyPress)
})

onUnmounted(() => {
  window.removeEventListener('keydown', handleKeyPress)
})

/* Global styles for child components */
.tab-content {
  flex: 1;
  overflow: hidden;
  padding: 16px;
  display: flex;
  gap: 16px;
}

.container {
  display: grid;
  grid-template-columns: 280px 300px 420px;
  gap: 16px;
  height: 100%;
  flex-shrink: 0;
}

.image-area {
  flex: 1;
  transition: grid-template-columns 0.3s ease;
  display: grid;
  grid-template-columns: 1fr 420px;
  gap: 16px;
  height: 100%;
  overflow: hidden;
}

.advanced-panel,
.params-panel,
.prompt-panel,
.preview-panel,
.history-panel {
  background: white;
  border-radius: 8px;
  border: 1px solid #e0e0e0;
  box-shadow: 0 2px 8px rgba(0,0,0,0.1);
  display: flex;
  flex-direction: column;
  height: 100%;
  overflow: hidden;
}

.panel-header {
  flex-shrink: 0;
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: 12px;
  padding: 12px 16px;
  border-bottom: 2px solid #667eea;
  height: 50px;
}

.panel-title {
  margin: 0;
  font-size: 15px;
  font-weight: 600;
  color: #333;
  line-height: 1.2;
  display: flex;
  align-items: center;
  gap: 8px;
  flex-wrap: nowrap;
  white-space: nowrap;
}

.image-count {
  font-size: 12px;
  font-weight: 500;
  color: #667eea;
  background: rgba(102, 126, 234, 0.1);
  padding: 2px 8px;
  border-radius: 12px;
  margin-left: auto;
}

.panel-footer {
  flex-shrink: 0;
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: 12px;
  padding: 12px 16px;
  background: #f8f9fa;
  border-top: 1px solid #e0e0e0;
  height: 50px;
}

.panel-footer.center {
  justify-content: center;
}

.footer-title {
  font-size: 12px;
  font-weight: 600;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
