}

.footer-label {
  font-size: 14px;
  font-weight: 600;
  color: #666;
}

.footer-buttons {
  display: flex;
  gap: 8px;
}

.footer-btn {
  height: 32px;
  padding: 0 16px;
  border: none;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  color: white;
  border-radius: 6px;
