  font-weight: 600;
  cursor: pointer;
  transition: all 0.2s;
}

.footer-btn:hover:not(:disabled) {
  opacity: 0.9;
  transform: translateY(-1px);
}

.footer-btn:disabled {
  opacity: 0.5;
  cursor: not-allowed;
}

.header-buttons {
  display: flex;
  gap: 8px;
  align-items: center;
}

.infinite-btn-header {
  width: 40px;
  height: 32px;
  padding: 0;
  border: none;
  background: linear-gradient(135deg, #06b6d4 0%, #0891b2 100%);
  color: white;
  border-radius: 6px;
  font-size: 16px;
  font-weight: 700;
  cursor: pointer;
  transition: all 0.2s;
  display: flex;
  align-items: center;
  justify-content: center;
}

.infinite-btn-header:hover {
  opacity: 0.9;
  transform: scale(1.05);
}

.infinite-btn-header.active {
  background: linear-gradient(135deg, #10b981 0%, #059669 100%);
  animation: pulse-infinite 2s ease-in-out infinite;
}

.advanced-content,
.params-content {
  flex: 1;
  overflow-y: auto;
  padding: 16px;
}

.prompt-section {
  flex: 1;
  display: flex;
  flex-direction: column;
  overflow-y: auto;
  padding: 0 16px;
  min-height: 0;
}

.param-display {
  display: grid;
  grid-template-columns: repeat(5, 1fr);
  gap: 12px;
}

.param-item {
  text-align: center;
}

.param-item label {
  display: block;
  font-size: 11px;
  color: #666;
  margin-bottom: 4px;
}

.param-item .value {
  font-size: 14px;
  font-weight: 600;
  color: #667eea;
}

.section-title {
  margin: 12px 0 8px 0;
  font-size: 13px;
  font-weight: 600;
  color: #667eea;
}

.section-divider {
  height: 1px;
  background: #e0e0e0;
  margin: 16px 0 12px 0;
}

.form-group {
  margin-bottom: 12px;
}

.form-group.horizontal {
  display: flex;
  align-items: center;
  gap: 12px;
  margin-bottom: 10px;
}

.form-group.horizontal label {
  flex-shrink: 0;
  width: 85px;
  font-weight: 500;
  color: #333;
  font-size: 12px;
  margin: 0;
}

.form-group.horizontal input,
.form-group.horizontal select {
  flex: 1;
}

.form-group label {
  display: block;
  margin-bottom: 4px;
  font-weight: 500;
  color: #333;
  font-size: 13px;
}

/* Seed Random Button */
.seed-random-btn {
  width: 36px;
  height: 28px;
  padding: 0;
  border: none;
  background: linear-gradient(135deg, #10b981 0%, #059669 100%);
  color: white;
  border-radius: 6px;
  font-size: 16px;
  cursor: pointer;
  transition: all 0.2s;
  display: flex;
  align-items: center;
  justify-content: center;
  flex-shrink: 0;
}

.seed-random-btn:hover:not(:disabled) {
  opacity: 0.9;
  transform: scale(1.05);
}

.seed-random-btn:disabled {
  opacity: 0.5;
  cursor: not-allowed;
}

/* ADetailer Prompt Button */
.ad-prompt-btn {
  flex: 1;
  height: 28px;
  padding: 0 12px;
  border: none;
  background: linear-gradient(135deg, #f59e0b 0%, #f97316 100%);
  color: white;
  border-radius: 6px;
  font-size: 12px;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.2s;
}

.ad-prompt-btn:hover:not(:disabled) {
  opacity: 0.9;
  transform: translateY(-1px);
}

.ad-prompt-btn:disabled {
