  box-shadow: 0 2px 8px rgba(255, 107, 107, 0.4);
}

.tool-btn.queue-btn {
  background: linear-gradient(135deg, #f59e0b 0%, #d97706 100%);
  color: white;
}

.tool-btn.queue-btn:hover {
