  margin: 0;
  height: 32px;
  padding: 0 16px;
  font-size: 13px;
  display: flex;
  align-items: center;
  justify-content: center;
}

.lora-btn {
  background: linear-gradient(135deg, #f59e0b 0%, #f97316 100%);
}

/* Page Buttons (for slot pagination) */
.page-btn {
  min-width: 40px;
  height: 32px;
  padding: 0 12px;
  border: 2px solid #e0e0e0;
  background: white;
  color: #666;
  border-radius: 6px;
  font-size: 13px;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.2s;
  display: flex;
  align-items: center;
  justify-content: center;
}

.page-btn:hover {
  border-color: #667eea;
  color: #667eea;
  background: #f0f0ff;
  transform: translateY(-1px);
}

.page-btn.has-data {
  border-color: #667eea;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  color: white;
}

.page-btn.has-data:hover {
  opacity: 0.9;
}

.page-btn.active {
  border-width: 3px;
  box-shadow: 0 0 12px rgba(102, 126, 234, 0.6);
  transform: scale(1.05);
}

.page-btn.active.has-data {
  box-shadow: 0 0 16px rgba(102, 126, 234, 0.8);
}

/* Last Params Section */
.last-params-section {
