  padding: 4px 0;
  border-bottom: none;
}

.params-section-title:hover {
  color: #5568d3;
  background: rgba(102, 126, 234, 0.05);
}

.params-section-content {
  /* Container for all param groups */
}

.params-group {
  margin-bottom: 12px;
}

.params-group:last-child {
  margin-bottom: 0;
}

.params-group-label {
  font-size: 12px;
  font-weight: 800;
  color: #667eea;
  text-transform: uppercase;
  letter-spacing: 0.8px;
  margin-bottom: 8px;
  text-align: center;
  padding: 4px 0;
  background: rgba(102, 126, 234, 0.08);
  border-radius: 4px;
  cursor: pointer;
  user-select: none;
  transition: all 0.2s;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 6px;
}

.params-group-label:hover {
  background: rgba(102, 126, 234, 0.15);
  transform: translateY(-1px);
}

.params-group-label:active {
  transform: translateY(0);
}

.fold-icon {
  font-size: 10px;
  transition: transform 0.2s;
  display: inline-block;
}

.params-grid {
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 6px 12px;
}

.param-row {
  display: flex;
  align-items: center;
  justify-content: space-between;
  font-size: 12px;
}

.param-label {
  color: #666;
  font-weight: 500;
}

.param-value {
  color: #667eea;
  font-weight: 700;
  font-family: 'Consolas', 'Monaco', monospace;
}

.adetailer-row .param-value {
  color: #f59e0b;
  font-size: 11px;
}

.analyze-btn,
.load-btn {
  width: 100%;
  margin-top: 8px;
}

.generate-btn:hover,
.analyze-btn:hover,
.load-btn:hover {
  opacity: 0.9;
}

.generate-btn:disabled,
.analyze-btn:disabled {
  opacity: 0.5;
  cursor: not-allowed;
}

.progress-container {
  margin: 8px 16px;
  flex-shrink: 0;
}

.progress-bar {
  width: 100%;
  height: 20px;
  background: #f0f0f0;
  border-radius: 10px;
  overflow: hidden;
}

.progress-fill {
  height: 100%;
  background: linear-gradient(90deg, #667eea 0%, #764ba2 100%);
  transition: width 0.3s;
}

.progress-text {
  text-align: center;
  margin-top: 4px;
  font-size: 11px;
  color: #666;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 10px;
}

.progress-state {
  color: #667eea;
  font-weight: 600;
  font-size: 12px;
}

.progress-percent {
  font-weight: 600;
  color: #333;
}

.infinite-indicator {
  color: #667eea;
  font-weight: 600;
  font-size: 12px;
}

/* Generation Controls */
.generation-controls {
  display: flex;
  gap: 8px;
  padding: 8px 16px;
  background: #fffbeb;
  border-bottom: 1px solid #fde68a;
  flex-shrink: 0;
  justify-content: center;
}

.control-btn {
  height: 32px;
  padding: 0 16px;
  border: none;
  border-radius: 6px;
  font-size: 12px;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.2s;
  display: flex;
  align-items: center;
  justify-content: center;
  white-space: nowrap;
}

.control-btn.interrupt-btn {
  background: #ef4444;
  color: white;
}

.control-btn.interrupt-btn:hover:not(:disabled) {
  background: #dc2626;
}

.control-btn.skip-btn {
  background: #f59e0b;
  color: white;
}

.control-btn.skip-btn:hover:not(:disabled) {
  background: #d97706;
}

.control-btn.pause-btn {
  background: #06b6d4;
  color: white;
}

.control-btn.pause-btn:hover:not(:disabled) {
  background: #0891b2;
}

.control-btn.infinite-btn {
  background: #667eea;
  color: white;
}

.control-btn.infinite-btn:hover:not(:disabled) {
  background: #5568d3;
}

.control-btn.infinite-btn.active {
  background: #10b981;
  animation: pulse-infinite 2s ease-in-out infinite;
}

@keyframes pulse-infinite {
  0%, 100% {
    box-shadow: 0 0 0 0 rgba(16, 185, 129, 0.7);
  }
  50% {
    box-shadow: 0 0 0 8px rgba(16, 185, 129, 0);
  }
}

.control-btn:disabled {
  opacity: 0.5;
  cursor: not-allowed;
}

.preview-panel {
  overflow: hidden;
}

.preview-main {
  flex: 1;
  display: flex;
  align-items: center;
  justify-content: center;
  overflow: hidden;
  background: #fafafa;
  position: relative;
  transition: all 0.3s;
}

.preview-main.drag-over {
  background: linear-gradient(135deg, rgba(102, 126, 234, 0.1) 0%, rgba(118, 75, 162, 0.1) 100%);
  border: 3px dashed #667eea;
}

.preview-main.loading-png {
  background: #fff8e1;
}

.preview-main img {
  max-width: 100%;
  max-height: 100%;
  object-fit: contain;
}

.preview-placeholder {
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  gap: 12px;
  color: #999;
  font-size: 14px;
}

.preview-placeholder.loading {
  color: #ff8c00;
  font-weight: 500;
  animation: pulse 1.5s ease-in-out infinite;
}

.drop-hint {
  font-size: 12px;
  color: #667eea;
  padding: 8px 16px;
  background: rgba(102, 126, 234, 0.1);
  border-radius: 6px;
  border: 1px dashed #667eea;
