})

const filteredLoras = computed(() => {
  let filtered = loras.value

  // Filter by category
  if (selectedCategory.value !== 'all') {
    filtered = filtered.filter(lora => getCategory(lora) === selectedCategory.value)
  }

  // Filter by search query
