// Number validation helper
function validateNumber(value, min, max, defaultValue, step = null) {
  let num = Number(value)

  // NaN ì²´í¬
  if (isNaN(num)) {
    return defaultValue
  }

  // ë²”ìœ„ ì²´í¬
  if (num < min) num = min
  if (num > max) num = max

  // Step ì²´í¬ (width, height ë“±)
  if (step && num % step !== 0) {
    num = Math.round(num / step) * step
  }

  return num
}

