function handleFileChange(event) {
  const file = event.target.files[0]
  if (file && file.type === 'image/png') {
    pngInfoFile.value = file
  } else {
    props.showToast('PNG íŒŒì¼ë§Œ ì„ íƒí•´ì£¼ì„¸ìš”!', 'error')
