/**
 * Analyze PNG info using WebUI API
 */
async function analyzePngInfo() {
  if (!pngInfoFile.value) {
    props.showToast('PNG íŒŒì¼ì„ ì„ íƒí•´ì£¼ì„¸ìš”!', 'error')
    return
  }

  const file = pngInfoFile.value
  const reader = new FileReader()

  reader.onload = async (e) => {
    try {
      const base64 = e.target.result.split(',')[1]

      const response = await fetch(`${API_URL}/sdapi/v1/png-info`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          image: base64
        })
      })

      if (!response.ok) {
        throw new Error(`API ì—ëŸ¬: ${response.status}`)
      }

      const data = await response.json()
      pngInfoResult.value = data
    } catch (error) {
      console.error('PNG Info ë¶„ì„ ì‹¤íŒ¨:', error)

      let message = 'PNG Info ë¶„ì„ ì‹¤íŒ¨'
