/**
 * Refresh LoRA list
 */
async function refreshLoras() {
  loading.value = true
  try {
    // Call API to refresh LoRA file list on backend
    const response = await fetch(`${API_URL}/sdapi/v1/refresh-loras`, {
      method: 'POST'
    })
    if (!response.ok) {
      throw new Error(`Refresh API error: ${response.status}`)
    }

    // Then fetch the updated list
    await fetchLoras(true)
    props.showToast?.('LoRA ëª©ë¡ì´ ê°±ì‹ ë˜ì—ˆìŠµë‹ˆë‹¤', 'success')
  } catch (error) {
    console.error('Failed to refresh LoRAs:', error)
    props.showToast?.('LoRA ëª©ë¡ ê°±ì‹  ì‹¤íŒ¨', 'error')
  } finally {
    loading.value = false
  }
}

/**
